#!/usr/bin/pythonimport randomimport math#import pypyodbcimport timeimport __init__ as imdef buffer_out(outcomp, outatt, incomp, inatt):	data = im.fetchdata(im.comps[outcomp],outatt)	dropdata(im.comps[incomp], data, inatt)	return;def dropdata(table, data, Parameters):####    # Write data in .mdb	im.compsdata[table][Parameters] = data	return;	def fetchtable(table):	return im.compsdata[table];	def fetchdata(table,Parameters):####    #Read from .mdb    return im.compsdata[table][Parameters];def scan_connection():    rows = im.conn[list(im.conn)[0]]# access to first connector table    for d in rows:        buffer_out(d[0], d[1], d[2], d[3])    return;def setsuccess(table):####	dropdata(table, 1, 'Hardware')	scan_connection()	return;def setfail(table):####	dropdata(table, 0, 'Hardware')	scan_connection()	return;def statecomp(Component_list):####    s = []    for i1 in Component_list:        s.append(int(fetchdata(i1, 'Hardware')))    return s;